.pdp-swiper-mobile .swiper-pagination {
  background-color: #F5F5F5;
  border-radius: 8px;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 6px 12px;
  gap: 8px;
  width: max-content; /* prevents stretching */
  margin: 1rem auto 0 auto;
  position: relative;
}

/* Inactive bullets */
.pdp-swiper-mobile .swiper-pagination-bullet {
  opacity: 0.3;
  border-radius: 50%;
  transition: all 0.3s ease;
  border: 1px solid #064760;
}

/* Active bullet */
.pdp-swiper-mobile .swiper-pagination-bullet-active {
    background-color: transparent;
    border: 1px solid #064760;
  opacity: 1;
  transform: scale(1.4);
}
